def open_gui():
    subprocess.Popen(
        ["python", "/home/adi/matcha/ngetik/code/ritme-tidur/build/gui.py"])
    window.destroy()


def submit():
    print(entry_1.get(), "entry 1")
    print(entry_2.get(), "entry 2")
    jam = entry_1.get() if entry_1.get() else "00"
    menit = entry_2.get() if entry_2.get() else "00"
    hasil = hitung_waktu_tidur(jam, menit)
    jam1.config(text=hasil[0]["jam"])
    jam2.config(text=hasil[1]["jam"])
    jam3.config(text=hasil[2]["jam"])
    jam4.config(text=hasil[3]["jam"])

    siklus1.config(text=f"{hasil[0]['siklus']} siklus tidur")
    siklus2.config(text=f"{hasil[1]['siklus']} siklus tidur")
    siklus3.config(text=f"{hasil[2]['siklus']} siklus tidur")
    siklus4.config(text=f"{hasil[3]['siklus']} siklus tidur")

    durasi1.config(text=f"{hasil[0]['durasi']} jam")
    durasi2.config(text=f"{hasil[1]['durasi']} jam")
    durasi3.config(text=f"{hasil[2]['durasi']} jam")
    durasi4.config(text=f"{hasil[3]['durasi']} jam")
    print(hasil)


